// components/Layout.js
function Layout(props) {
    return (
        <div className="page-layout">
            {props.children}
            <style jsx global>{`

        h1, .lead, .checkMail_section h4 , h3 {
            font-family: 'Signika', sans-serif;
        }

        .header_logo {
            margin-top: 5%;
        }

        input:focus {
        transition: all 0.8s ease-in-out;
        }

        .lead {
            margin-left: 2%;
        }

        .select-box {
            cursor: pointer;
            max-width:  20em;
        }
        
        .select,
        .label {
            color: #414141;
            font: 400 13.3333px Arial;
            margin: 0;
            padding: 0;
            text-align: start;
            align-items: center;
        }
        
        .select {
            height: 40px;
            border: 0 none;
        }
            
          .signup_form {
              width: 75%;
          }

          .signup_form-email:focus {
            transition: all .4s ease-in-out;
            font-size: 110%:
          }

        .header_selectBox {
            left: 5%;
            line-height: 1.15;
            border-radius: 25px;
            padding: 0px 6px;
        }

        .header_social {
            right: 5%;
        }

        .spacing {
            letter-spacing: .5px ;
        }
        
        .lighter {
            font-weight : lighter;
        }

        .checkMail_section {
            background-color: #25d366 ;
        }

        .checkMail_section-btn {
            background-color: white;
            border-radius: 6px;
            color: #343a40;
            transition: all .5s ease-in-out;
            
        }

        .checkMail_section-btn:hover {
            background-color: #25d366;
            color: white;
            font-weight: bold;
            transition: all .5s ease-in-out;
        }

        .extraMargin {
            margin-top: 1%;
            margin-bottom: .5%;
        }

        .footer {
            background-color: #444444;
        }

        .full-width {
            width: 100%;
            color: white;
        }

        .white-icon {
            color: white;
        }

        .footer-large {
            padding: 0 !important
        }

        .footer h3 {
            color: white;
            text-align: center;
            margin-left: 3%;
        }


        .content {
            transform: translate(-50%, -50%);
            height: 160px;
            overflow: hidden;
            font-family: 'Lato', sans-serif;
            font-size: 35px;
            line-height: 40px;
            color: black;
       }
        .content__container {
            font-weight: 600;
            overflow: hidden;
            height: 40px;
            padding: 0 40px;
       }
        .content__container:after, .content__container:before {
            color: black;
            font-size: 42px;
            line-height: 40px;
            -webkit-animation-name: opacity;
            -webkit-animation-duration: 2s;
            -webkit-animation-iteration-count: infinite;
            animation-name: opacity;
            animation-duration: 2s;
            animation-iteration-count: infinite;
       }
        
        .content__container__list {
            text-align: left;
            list-style: none;
            -webkit-animation-name: change;
            -webkit-animation-duration: 10s;
            -webkit-animation-iteration-count: infinite;
            animation-name: change;
            animation-duration: 10s;
            animation-iteration-count: infinite;
       }
        .content__container__list__item {
            line-height: 40px;
            margin: 0;
       }
        @-webkit-keyframes opacity {
            0%, 100% {
                opacity: 0;
           }
            50% {
                opacity: 1;
           }
       }
        @-webkit-keyframes change {
            0%, 50%, 100% {
                transform: translate3d(0, 0, 0);
           }
            33.32%, 45.98% {
                transform: translate3d(0, 50%, 0);
           }
            66.64%, 79.3% {
                transform: translate3d(0, 100%, 0);
           }
            83.3%, 95.96% {
                transform: translate3d(0, -50%, 0);
           }
       }
        @-o-keyframes opacity {
            0%, 100% {
                opacity: 0;
           }
            50% {
                opacity: 1;
           }
       }
        @-o-keyframes change {
            0%, 12.66%, 100% {
                transform: translate3d(0, 0, 0);
           }
            16.66%, 29.32% {
                transform: translate3d(0, -25%, 0);
           }
            33.32%, 45.98% {
                transform: translate3d(0, -50%, 0);
           }
            49.98%, 62.64% {
                transform: translate3d(0, -75%, 0);
           }
            66.64%, 79.3% {
                transform: translate3d(0, -50%, 0);
           }
            83.3%, 95.96% {
                transform: translate3d(0, -25%, 0);
           }
       }
        @-moz-keyframes opacity {
            0%, 100% {
                opacity: 0;
           }
            50% {
                opacity: 1;
           }
       }
        @-moz-keyframes change {
            0%, 12.66%, 100% {
                transform: translate3d(0, 0, 0);
           }
            16.66%, 29.32% {
                transform: translate3d(0, -25%, 0);
           }
            33.32%, 45.98% {
                transform: translate3d(0, -50%, 0);
           }
            49.98%, 62.64% {
                transform: translate3d(0, -75%, 0);
           }
            66.64%, 79.3% {
                transform: translate3d(0, -50%, 0);
           }
            83.3%, 95.96% {
                transform: translate3d(0, -25%, 0);
           }
       }
        @keyframes opacity {
            0%, 100% {
                opacity: 0;
           }
            50% {
                opacity: 1;
           }
       }
        @keyframes change {
            0%, 12.66%, 100% {
                transform: translate3d(0, 0, 0);
           }
            16.66%, 29.32% {
                transform: translate3d(0, -25%, 0);
           }
            33.32%, 45.98% {
                transform: translate3d(0, -50%, 0);
           }
            49.98%, 62.64% {
                transform: translate3d(0, -75%, 0);
           }
            66.64%, 79.3% {
                transform: translate3d(0, -50%, 0);
           }
            83.3%, 95.96% {
                transform: translate3d(0, -25%, 0);
           }
       }
        
        

        `}</style>
        </div>
    )
}

export default Layout
